#!/bin/bash# installation script for DPL Digital Preservation Lab -- an environment for teaching and experimenting digital preservation# runs on Ubuntu 24.04# 2025 Tobias Wildi, tobias.wildi@hultarp.org## Use this script to install the tools on new Ubuntu-machine# Exit immediately if a command exits with a non-zero statusset -e# Needed for all installerssudo apt update -ysudo apt install curl zip unzip -y    # Set keyboard layout to Swiss German    gsettings set org.gnome.desktop.input-sources sources "[('xkb', 'ch')]"# create ~/opt for additional softareif [ -d ~/opt ]; then    echo "Directory ~/opt already exists."else    mkdir ~/opt    echo "Directory ~/opt created."fi# Ensure computer doesn't go to sleep or lock while installinggsettings set org.gnome.desktop.screensaver lock-enabled falsegsettings set org.gnome.desktop.session idle-delay 0# Run installersfor script in ~/dpl/install/*.sh; do source $script; done# Upgrade everything that might ask for a reboot lastsudo apt upgrade -y# Revert to normal idle and lock settingsgsettings set org.gnome.desktop.screensaver lock-enabled truegsettings set org.gnome.desktop.session idle-delay 300# Logout to pickup changesgum confirm "Ready to logout for all settings to take effect?" && gnome-session-quit --logout --no-promptexit 0